mapper.select:
select
  UserID, GameID, ProtectID, PasswordID, SpreaderID, Accounts, NickName, RegAccounts, UnderWrite, PassPortID, Compellation, LogonPass, InsurePass, DynamicPass, DynamicPassTime, FaceID, CustomID, Present, UserMedal, Experience, GrowLevelID, LoveLiness, UserRight, MasterRight, ServiceRight, MasterOrder, MemberOrder, MemberOverDate, MemberSwitchDate, CustomFaceVer, Gender, Nullity, NullityOverDate, StunDown, MoorMachine, IsAndroid, WebLogonTimes, GameLogonTimes, PlayTimeCount, OnLineTimeCount, LastLogonIP, LastLogonDate, LastLogonMobile, LastLogonMachine, RegisterIP, RegisterDate, RegisterMobile, RegisterMachine, RegisterOrigin, PlatformID, UserUin, RankID, AgentID, ParentID, UserType, Advertiser, LastLogonIPAddress, AdvertPlat, LastLogoOrigin, RegisterMobileDate
from dbo.AccountsInfo


        and UserID = #{userid}


        and GameID = #{gameid}


        and ProtectID = #{protectid}


        and PasswordID = #{passwordid}


        and SpreaderID = #{spreaderid}


        and Accounts = #{accounts}


        and NickName = #{nickname}


        and RegAccounts = #{regaccounts}


        and UnderWrite = #{underwrite}


        and PassPortID = #{passportid}


        and Compellation = #{compellation}


        and LogonPass = #{logonpass}


        and InsurePass = #{insurepass}


        and DynamicPass = #{dynamicpass}


        and DynamicPassTime = #{dynamicpasstime}


        and FaceID = #{faceid}


        and CustomID = #{customid}


        and Present = #{present}


        and UserMedal = #{usermedal}


        and Experience = #{experience}


        and GrowLevelID = #{growlevelid}


        and LoveLiness = #{loveliness}


        and UserRight = #{userright}


        and MasterRight = #{masterright}


        and ServiceRight = #{serviceright}


        and MasterOrder = #{masterorder}


        and MemberOrder = #{memberorder}


        and MemberOverDate = #{memberoverdate}


        and MemberSwitchDate = #{memberswitchdate}


        and CustomFaceVer = #{customfacever}


        and Gender = #{gender}


        and Nullity = #{nullity}


        and NullityOverDate = #{nullityoverdate}


        and StunDown = #{stundown}


        and MoorMachine = #{moormachine}


        and IsAndroid = #{isandroid}


        and WebLogonTimes = #{weblogontimes}


        and GameLogonTimes = #{gamelogontimes}


        and PlayTimeCount = #{playtimecount}


        and OnLineTimeCount = #{onlinetimecount}


        and LastLogonIP = #{lastlogonip}


        and LastLogonDate = #{lastlogondate}


        and LastLogonMobile = #{lastlogonmobile}


        and LastLogonMachine = #{lastlogonmachine}


        and RegisterIP = #{registerip}


        and RegisterDate = #{registerdate}


        and RegisterMobile = #{registermobile}


        and RegisterMachine = #{registermachine}


        and RegisterOrigin = #{registerorigin}


        and PlatformID = #{platformid}


        and UserUin = #{useruin}


        and RankID = #{rankid}


        and AgentID = #{agentid}


        and ParentID = #{parentid}


        and UserType = #{usertype}


        and Advertiser = #{advertiser}


        and LastLogonIPAddress = #{lastlogonipaddress}


        and AdvertPlat = #{advertplat}


        and LastLogoOrigin = #{lastlogoorigin}


        and RegisterMobileDate = #{registermobiledate}
